#include <stdio.h>
#include <string.h>
#include <ctype.h>

void vigenere_decrypt(char *ciphertext, char *key, char *plaintext) {
    int ciphertext_len = strlen(ciphertext);
    int key_len = strlen(key);
    int i, j;

    for (i = 0; i < ciphertext_len; i++) {
        int c = ciphertext[i];
        int k = key[i % key_len];
        int shift = isalpha(c) ? isupper(c) ? 'A' : 'a' : '0';
        int p = (c - shift - (k % 26) + 26) % 26 + shift;
        plaintext[i] = (char) p;
    }
    plaintext[ciphertext_len] = '\0';
}

